let PrismaClient;_a10‍.x([["findAll",()=>findAll],["create",()=>create],["update",()=>update],["remove",()=>remove]]);_a10‍.w("@prisma/client",[["PrismaClient",["PrismaClient"],function(v){PrismaClient=v}]]);

const prisma = new PrismaClient();

       const findAll = async (req, res) => {
  try {
    const products = await prisma.product.findMany();
    res.json({
      ok: true,
      data: products,
    });
  } catch (error) {
    res.json({
      ok: false,
      data: error.message,
    });
  }
};

       const create = async (req, res) => {
  try {
    const { body } = req;
    const product = await prisma.product.create({
      data: {
        ...body,
      },
    });
    res.json({
      ok: true,
      data: product,
    });
  } catch (error) {
    res.json({
      ok: false,
      data: error.message,
    });
  }
};

       const update = async (req, res) => {
  try {
    const id = Number(req.params.id)
    const product = await prisma.product.update({
      where: {id},
      data: req.body,
    })
    res.json({
      ok: true,
      data: product,
    });
  } catch (error) {
    res.json({
      ok: false,
      data: error.message,
    });
  }
}

       const remove = async (req, res) => {
  try {
    const id = Number(req.params.id);
    const product = await prisma.product.delete({
      where: { id },
    });
    res.json({
      ok: true,
      data: product,
    });
  } catch (error) {
    res.json({
      ok: "Delete student",
      data: error.message,
    });
  }
};
